function z = AitkenPol(x,y,a)err=1e-3; [z, I] = sort(x-a);x = x(I);y = y(I);n=length(x);ak=zeros(n,n);ak(:,1)=y';for i=1:n    for j=1:i-1       #i       #j       xj = z(j);       xi = z(i);       fjj = ak(j,j);       fij = ak(i,j);       up = xi*fjj - xj*fij;       down = x(i)-x(j);       ak(i, j+1) = up/down;    end        if(i<=1)      continue    endif        current_err = ak(i,i) - ak(i-1, i-1);    current_err = abs(current_err);        if(current_err>err)      continue    endif        z = ak(i,i)    returnend